def generate_session_description(session_data):
    """Generate a meaningful description for a session based on its activity"""
    try:
        # Try to use the activity analyzer for better descriptions
        try:
            from activity_analyzer import ActivityAnalyzer
            from memory_manager import MemoryManager
            analyzer = ActivityAnalyzer()

            # Get detailed session data for analysis
            session_id = session_data.get('session_id')
            if session_id:
                # Try to load full session data from archived sessions first
                memory_dir = Path(__file__).parent.parent
                archived_file = memory_dir / "sessions" / "archived" / f"{session_id}.json"

                full_session_data = None
                if archived_file.exists():
                    with open(archived_file, 'r') as f:
                        full_session_data = json.load(f)
                else:
                    # Try active session file
                    active_file = memory_dir / "sessions" / "active" / "current-session.json"
                    if active_file.exists():
                        with open(active_file, 'r') as f:
                            temp_data = json.load(f)
                            if temp_data.get('id') == session_id:
                                full_session_data = temp_data

                if full_session_data:
                    # Get additional data from database through memory manager
                    try:
                        mm = MemoryManager()
                        tool_usage = mm.db.get_session_tool_usage(session_id)
                        file_interactions = full_session_data.get('file_interactions', [])

                        # Prepare data for activity analyzer
                        analyzer_data = {
                            'tool_usage': tool_usage,
                            'file_interactions': file_interactions,
                            'session_id': session_id
                        }

                        # Analyze the session
                        analysis = analyzer.analyze_session_activity(analyzer_data)

                        # Return the human-readable description if confident
                        if analysis.get('confidence_score', 0) > 0.5:
                            return analysis['human_description']
                    except Exception as db_error:
                        logging.debug(f"Database access failed, using fallback: {db_error}")

        except Exception as analyzer_error:
            logging.debug(f"Activity analyzer failed, falling back to simple description: {analyzer_error}")

        # Fallback to original logic if analyzer fails
        tool_count = session_data.get('tool_usage_count', 0)
        file_count = session_data.get('file_interaction_count', 0)
        tools_used = session_data.get('unique_tools_used', [])

        # Generate enhanced descriptions based on activity patterns
        if file_count > 0 and tool_count > 0:
            # Enhanced file modification descriptions
            if 'Write' in tools_used and 'Edit' in tools_used:
                if file_count == 1:
                    return f"Created and refined single file ({tool_count} operations)"
                else:
                    return f"File creation & editing project ({file_count} files, {tool_count} operations)"
            elif 'Write' in tools_used:
                if file_count == 1:
                    return f"New file development ({tool_count} operations)"
                else:
                    return f"Multi-file creation project ({file_count} files)"
            elif 'Edit' in tools_used or 'MultiEdit' in tools_used:
                if file_count == 1:
                    return f"Focused file editing ({tool_count} operations)"
                else:
                    return f"Code maintenance across {file_count} files"
            elif 'Read' in tools_used and tool_count > 5:
                return f"Code analysis and exploration ({tool_count} operations)"
            else:
                return f"File operations on {file_count} files"
        elif tool_count > 15:
            # Heavy exploration or command execution
            if 'Read' in tools_used and 'Grep' in tools_used:
                return f"Extensive codebase exploration ({tool_count} operations)"
            elif 'Bash' in tools_used:
                return f"Command-heavy development session ({tool_count} operations)"
            else:
                return f"Intensive development work ({tool_count} operations)"
        elif tool_count > 5:
            # Medium activity session  
            if 'Read' in tools_used and 'Grep' in tools_used:
                return f"Code investigation and analysis"
            elif 'Bash' in tools_used:
                return f"Command execution and testing"
            elif 'Read' in tools_used:
                return f"Documentation and code review"
            else:
                return f"Development session ({tool_count} operations)"
        elif tool_count > 0:
            # Light activity session
            if 'Read' in tools_used:
                return f"Quick file review ({tool_count} operations)"
            elif 'Bash' in tools_used:
                return f"Command execution ({tool_count} operations)"
            else:
                return f"Light development work ({tool_count} operations)"
        else:
            return "Empty session"
    except Exception as e:
        logging.debug(f"Failed to generate session description: {e}")
        return "Development session"

